import { Injectable } from '@angular/core';
import { Auth, signInWithEmailAndPassword, signOut } from '@angular/fire/auth';
import { doc , docData, Firestore, updateDoc, getFirestore} from '@angular/fire/firestore';
import { createUserWithEmailAndPassword,getAuth } from '@firebase/auth';
import {Photo} from '@capacitor/camera';
import {ref, Storage} from '@angular/fire/storage';
import { getDownloadURL, uploadString } from '@firebase/storage';
import { url } from 'inspector';
import { ToastController } from '@ionic/angular';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private auth: Auth,
    private firestore: Firestore,
    private toastController: ToastController,
    private storage: Storage) { }
async register({email,password}){
  //using firebase authentication to enable the user to register
 try{
    const user = await  createUserWithEmailAndPassword(this.auth,email,password);
    return user;

  }catch (e){
    const toast = await this.toastController.create({
      message:
        // eslint-disable-next-line max-len
      e.code,
      duration: 5000,
    });
    await toast.present();
return null;
  }
}


async login({email,password}){
  //loging using email and password try catch in order to be able to know if login successfull or not
   try{
     const user = await  signInWithEmailAndPassword(this.auth,email,password);
     return user;
   }catch (e){
     return null;
   }
 }

 logout(){
  //logout from account
 return signOut(this.auth);
  }

  getCurrentUserId(){
  const auth = getAuth();
  const user = auth.currentUser;
  if (user) {
  //this returns the authenticated user (uid, email....)
 const id = user.uid;
  return id;


  } else {
console.log('no user signed');
    // No user is signed in.
  }
}

 getUserById(id){
  const userRef = doc(this.firestore,`Users/${id}`);
    return docData(userRef, {idField : 'id'});

}
//letting the user upload their image
async uploadImage(cameraFile: Photo){
 const user = this.auth.currentUser;
 const path = `uploads/${user.uid}/profile.png`;
 const storageRef = ref(this.storage,path);
 try{
  await uploadString(storageRef,cameraFile.base64String,'base64');
  const imageUrl = await getDownloadURL(storageRef);
  const userRef = doc(this.firestore,`Users/${user.uid}`);
  await updateDoc(userRef,{
    imageUrl,
  });
  return true;
 }catch(error){
  return false;
 }
}

async uploadDefaultImage(){
//this method uploads the photo to the firebase storage
//and assigns it by id to the user, it also updates the user document to link it to
//the photo URL

 // file is the base64 format of the default image when a user logs in
  // eslint-disable-next-line max-len
  const file = '';

  const user = this.auth.currentUser; //getting the current user that is signing up
 const path = `uploads/${user.uid}/profile.png`;
 const storageRef = ref(this.storage,path); //firebase storage
 try{
  await uploadString(storageRef,file,'base64');
  const imageUrl = await getDownloadURL(storageRef);
  const userRef = doc(this.firestore,`Users/${user.uid}`);
  await updateDoc(userRef,{
    imageUrl,
  });
  return true;
 }catch(error){
  return false;
 }
}


}










